INSERT INTO `news` (`id`, `title`, `summary`, `description`, `cat_id`, `location`, `image`, `news_date`, `reporter_id`, `is_sticky`, `status`, `added_by`, `created_at`, `updated_at`) VALUES
(10, 'उपत्यकामा राति १० बजेसम्म गाडी', 'काठमाडौँ — काठमाडौं महानगरले राजधानीमा चल्ने सार्वजनिक यातायातका गाडीहरू राति १० बजेसम्म सञ्चालन गर्नुपर्ने अनिवार्य गरेको छ । अबको १५ दिनमा ऐन प्रमाणीकरण भई राजपत्रमा प्रकाशित भएपछि राति १० बजेसम्म सार्वजनिक साधन अनिवार्य सञ्चालन गर्नुपर्ने व्यवस्था लागू हुने जानकारी महानगरले दिएको छ ।', '&lt;p&gt;काठमाडौँ &mdash; काठमाडौं महानगरले राजधानीमा चल्ने सार्वजनिक यातायातका गाडीहरू राति १० बजेसम्म सञ्चालन गर्नुपर्ने अनिवार्य गरेको छ । अबको १५ दिनमा ऐन प्रमाणीकरण भई राजपत्रमा प्रकाशित भएपछि राति १० बजेसम्म सार्वजनिक साधन अनिवार्य सञ्चालन गर्नुपर्ने व्यवस्था लागू हुने जानकारी महानगरले दिएको छ ।&amp;nbsp;&lt;br&gt;काठमाडौं महानगरले सवारी तथा यातायात व्यवस्थापन ऐन २०७५ जारी गरी महानगरभित्रका रुटमा सञ्चालन हुने सार्वजनिक सवारी साधनले बिहान ५ देखि राति १० बजेसम्म सेवा सञ्चालन गर्नुपर्ने व्यवस्था गरेको हो । कात्तिकदेखि माघ मसान्तसम्म भने बसले बिहान ५ देखि राति ९ बजेसम्म सेवा दिनुपर्ने ऐनमा व्यवस्था गरिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा महानगरसँग स्वीकृति नलिई सार्वजनिक सवारी साधनमा कुनै विज्ञापन राख्न नपाइने, बसमा फोहोर फाल्ने भाँडो, अग्नि नियन्त्रक उपकरण अनिवार्य रूपमा राख्नुपर्ने व्यवस्था गरिएको छ ।&amp;nbsp;&lt;br&gt;चालक र सहचालकलाई पोसाक उपलब्ध गराउने, यात्रुका गुनासो सुन्ने र समस्या समाधान गर्ने व्यवस्था मिलाउनुपर्ने व्यवस्थासमेत ऐनमा गरिएको जानकारी महानगरका अधिकारीले दिएका छन् । ती अधिकारीका अनुसार सार्वजनिक सवारी साधनमा हुने विज्ञापनलाई करको दायरामा ल्याउन ऐनमा त्यस्तो व्यवस्था गरिएको हो । सार्वजनिक सवारी साधनमा प्राथमिक उपचार सामग्री अनिवार्य रूपमा राख्नुपर्ने व्यवस्थासमेत गरिएको छ ।&amp;nbsp;&lt;br&gt;चालकले जथाभावी यात्रु चढाउने र ओराल्ने नगर्ने, अश्लील नबोल्ने, यौनजन्य दुर्व्यवहार नगर्ने, मदिरा र सुर्तीजन्य वस्तुको प्रयोग नगर्ने दायित्व निर्वाह गर्नुपर्ने र नगरेमा कारबाही हुने व्यवस्थासमेत ऐनमा गरिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा मदिरा तथा सुर्तीजन्य वस्तुको सेवन नहुने, अश्लील बोल्न रोक लगाउने, यौनजन्य हिंसा हुन नदिने दायित्व सहचालकलाई हुने र भएमा कारबाही हुने व्यवस्था राखिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा यात्रुले अनिवार्य टिकट लिनुपर्ने, कागजी टिकटलाई विद्युतीय टिकटले प्रतिस्थापन गर्नुपर्ने, तोकिएको स्थानमा मात्र यात्रु चढाउने र ओराल्ने व्यवस्था अनिवार्य लागू गरिने भएको छ ।&lt;/p&gt;', 1, 'काठमाडौँ', 'news-20190408052833425.jpg', '२०७५-चैत-२५', 2, 1, 'published', 1, '2019-04-08 09:13:33', NULL),
(11, 'राति १० बजेसम्म गाडी', 'काठमाडौँ — काठमाडौं महानगरले राजधानीमा चल्ने सार्वजनिक यातायातका गाडीहरू राति १० बजेसम्म सञ्चालन गर्नुपर्ने अनिवार्य गरेको छ । अबको १५ दिनमा ऐन प्रमाणीकरण भई राजपत्रमा प्रकाशित भएपछि राति १० बजेसम्म सार्वजनिक साधन अनिवार्य सञ्चालन गर्नुपर्ने व्यवस्था लागू हुने जानकारी महानगरले दिएको छ । ', '&lt;p&gt;काठमाडौँ &mdash; काठमाडौं महानगरले राजधानीमा चल्ने सार्वजनिक यातायातका गाडीहरू राति १० बजेसम्म सञ्चालन गर्नुपर्ने अनिवार्य गरेको छ । अबको १५ दिनमा ऐन प्रमाणीकरण भई राजपत्रमा प्रकाशित भएपछि राति १० बजेसम्म सार्वजनिक साधन अनिवार्य सञ्चालन गर्नुपर्ने व्यवस्था लागू हुने जानकारी महानगरले दिएको छ ।&amp;nbsp;&lt;br&gt;काठमाडौं महानगरले सवारी तथा यातायात व्यवस्थापन ऐन २०७५ जारी गरी महानगरभित्रका रुटमा सञ्चालन हुने सार्वजनिक सवारी साधनले बिहान ५ देखि राति १० बजेसम्म सेवा सञ्चालन गर्नुपर्ने व्यवस्था गरेको हो । कात्तिकदेखि माघ मसान्तसम्म भने बसले बिहान ५ देखि राति ९ बजेसम्म सेवा दिनुपर्ने ऐनमा व्यवस्था गरिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा महानगरसँग स्वीकृति नलिई सार्वजनिक सवारी साधनमा कुनै विज्ञापन राख्न नपाइने, बसमा फोहोर फाल्ने भाँडो, अग्नि नियन्त्रक उपकरण अनिवार्य रूपमा राख्नुपर्ने व्यवस्था गरिएको छ ।&amp;nbsp;&lt;br&gt;चालक र सहचालकलाई पोसाक उपलब्ध गराउने, यात्रुका गुनासो सुन्ने र समस्या समाधान गर्ने व्यवस्था मिलाउनुपर्ने व्यवस्थासमेत ऐनमा गरिएको जानकारी महानगरका अधिकारीले दिएका छन् । ती अधिकारीका अनुसार सार्वजनिक सवारी साधनमा हुने विज्ञापनलाई करको दायरामा ल्याउन ऐनमा त्यस्तो व्यवस्था गरिएको हो । सार्वजनिक सवारी साधनमा प्राथमिक उपचार सामग्री अनिवार्य रूपमा राख्नुपर्ने व्यवस्थासमेत गरिएको छ ।&amp;nbsp;&lt;br&gt;चालकले जथाभावी यात्रु चढाउने र ओराल्ने नगर्ने, अश्लील नबोल्ने, यौनजन्य दुर्व्यवहार नगर्ने, मदिरा र सुर्तीजन्य वस्तुको प्रयोग नगर्ने दायित्व निर्वाह गर्नुपर्ने र नगरेमा कारबाही हुने व्यवस्थासमेत ऐनमा गरिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा मदिरा तथा सुर्तीजन्य वस्तुको सेवन नहुने, अश्लील बोल्न रोक लगाउने, यौनजन्य हिंसा हुन नदिने दायित्व सहचालकलाई हुने र भएमा कारबाही हुने व्यवस्था राखिएको छ ।&amp;nbsp;&lt;br&gt;ऐनमा यात्रुले अनिवार्य टिकट लिनुपर्ने, कागजी टिकटलाई विद्युतीय टिकटले प्रतिस्थापन गर्नुपर्ने, तोकिएको स्थानमा मात्र यात्रु चढाउने र ओराल्ने व्यवस्था अनिवार्य लागू गरिने भएको छ ।&lt;/p&gt;', 1, 'काठमाडौँ', 'news-20190408052955597.jpg', '२०७५-चैत-२५', 2, 0, 'published', 1, '2019-04-08 09:14:55', '2019-04-08 11:20:50'),
